}

// GET tables/Message/48D68C86-6EA6-4C25-AA33-223FC9A27959
public SingleResult Message  GetMessage(string id)
{
    return new SingleResult Message (Lookup(id).Queryable.OwnedByFriends(context.Friends, UserId));
}

