JobDTO, Job
()                                            
                .ForMember(job =
 job.Customer, map =
 map.Ignore())
                .ForMember(job =
 job.Equipments, map =
 map.Ignore());

            cfg.CreateMap
Customer, CustomerDTO
();            
            cfg.CreateMap
Equipment, EquipmentDTO
();
        }
